mapper.select:
SELECT * FROM board
ORDER BY boardId DESC
LIMIT #{limit} OFFSET #{offset}

mapper.update:
UPDATE board
SET boardTitle = #{boardTitle}, boardWriter = #{boardWriter}, boardContent = #{boardContent}, updateTime = now(), boardImg = #{boardImg}
WHERE boardId = #{boardId}

UPDATE board
SET viewCnt = #{viewCnt}
WHERE boardId = #{boardId}
